
public class Nation {
	private String _name;
	private int _count;
	
	public Nation(String name, int i) {
		// TODO Auto-generated constructor stub
		_name = name;
		_count = i;
	}

	public String getName()
	{
		return _name;
	}
	
	public void setName(String arg)
	{
		_name = arg;
	}
	
	public int getCount()
	{
		return _count;
	}
	
	public void setCount(int arg)
	{
		_count = arg;
	}
}
